import ConsoleLogManager from './ConsoleLogManager';
import LogManager from './interfaces/LogManager';

export interface BaseServiceParams {
  serviceName: string;
}

export default class BaseService {
  public serviceName: string;

  public logger: LogManager;

  constructor(params: BaseServiceParams) {
    this.serviceName = params.serviceName;
    this.logger = new ConsoleLogManager({ name: this.serviceName });
  }
}
