import ConsoleLogManager from './ConsoleLogManager';
import LogManager from './interfaces/LogManager';

export interface BaseStoreParams {
  storeName: string;
}

export default class BaseStore {
  public storeName: string;

  public logger: LogManager;

  constructor(params: BaseStoreParams) {
    this.storeName = params.storeName;
    this.logger = new ConsoleLogManager({ name: this.storeName });
  }
}
