import LogManager, { LogManagerParams } from './interfaces/LogManager';

class ConsoleLogManager implements LogManager {
  public name: string;

  constructor(params: LogManagerParams) {
    this.name = params.name;
  }

  private header() {
    return `[${this.name}]:`;
  }

  log(...message: any[]) {
    console.log(`${this.header()}`, ...message);
  }

  info(...message: any[]) {
    console.info(`${this.header()}`, ...message);
  }

  warn(...message: any[]) {
    console.warn(`${this.header()}`, ...message);
  }

  error(...message: any[]) {
    console.error(`${this.header()}`, ...message);
  }
}

export default ConsoleLogManager;
