import z from 'zod';

export const zAnalyticsEvents = z.enum([
  // Workspace
  'CreateOrganization',
  'SendOrganizationInvite',
  'AcceptOrganizationInvite',
  'SetUserOrganization',
  // Bot
  'CreateBot',
  'EditBot',
  // Source
  'AddSource',
  'EditSource',
  // Sync
  'CreateSync',
  // Query
  'CreateQuery',
  'SetQueryFeedback',
  'AddQueryEdit',
]);

type AnalyticsEvents = z.infer<typeof zAnalyticsEvents>;

export default AnalyticsEvents;
