import z from 'zod';

export const youtubeSRTLine = z.object({
  text: z.string(),
  duration: z.number(), // offset in ms
  offset: z.number(), // duration in ms
});

export const srtLine = z.object({
  id: z.string(),
  startTime: z.string(),
  startSeconds: z.number(),
  endTime: z.string(),
  endSeconds: z.number(),
  text: z.string(),
  pageNumber: z.number().nullable(),
});

export type YouTubeSRTLine = z.infer<typeof youtubeSRTLine>;
export type SRTLine = z.infer<typeof srtLine>;
