import z from 'zod';

export const unstructuredMetadata = z.object({
  page_number: z.number(),
  filename: z.string(),
});

export const zTextTypes = z.enum([
  'Title',
  'ListItem',
  'NarrativeText',
  'UncategorizedText',
  'FigureCaption',
  'Body',
  'Header',
  'Footer',
]);

export const unstructuredElement = z.object({
  element_id: z.string(),
  text: z.string(),
  type: zTextTypes,
  metadata: unstructuredMetadata,
});

export const unstructuredSnippet = z.object({
  id: z.string(),
  text: z.string(),
  pageNumber: z.number(),
});

export type UnstructuredElement = z.infer<typeof unstructuredElement>;
export type UnstructuredSnippet = z.infer<typeof unstructuredSnippet>;
