import z from 'zod';

export const usage = z.object({
  credits: z.number(),
  documents: z.number(),
  syncs: z.number(),
});

export const updateUsage = z.object({
  credits: z.number().optional(),
  documents: z.number().optional(),
  syncs: z.number().optional(),
});
export type Usage = z.infer<typeof usage>;
export type UpdateUsage = z.infer<typeof updateUsage>;
