import shortid from 'shortid';
import Metadata from './Metadata.mongo';

const Server = {
  id: {
    type: String,
    required: true,
    unique: true,
  },
  name: {
    type: String,
    required: false,
  },
  registeredOn: {
    type: Number,
    required: true,
    default: Date.now().valueOf(),
  },
};
export default {
  _id: {
    type: String,
    required: true,
    default: shortid.generate,
  },
  botId: {
    type: String,
    required: true,
    default: null,
  },
  server: Server,
  meta: Metadata,
};
