import Metadata from './Metadata.mongo';

export default {
  _id: {
    type: String,
    required: true,
    // default: shortid.generate, should always be specified at time of creation
  },
  name: {
    type: String,
    required: true,
  },
  mimetype: {
    type: String,
    required: true,
  },
  encoding: {
    type: String,
    required: true,
  },
  sizeBytes: {
    type: Number,
    required: false,
    default: null,
  },
  durationSeconds: {
    type: Number,
    required: false,
    default: null,
  },
  url: {
    type: String,
    required: true,
  },
  meta: Metadata,
};
