import shortid from 'shortid';
import Metadata from './Metadata.mongo';

export default {
  _id: {
    type: String,
    required: true,
    default: shortid.generate,
  },
  organizationId: {
    type: String,
    required: true,
    default: null,
  },
  name: {
    type: String,
    required: true,
  },
  key: {
    type: String,
    required: true,
  },
  lastUsedAt: {
    type: String,
    required: false,
    default: null,
  },
  archived: {
    type: Boolean,
    required: false,
    default: false,
  },
  meta: Metadata,
};
