import shortid from 'shortid';
import Metadata from './Metadata.mongo';

export default {
  _id: {
    type: String,
    required: true,
    default: shortid.generate,
  },
  organizationId: {
    type: String,
    required: true,
    default: null,
  },
  userId: {
    type: String,
    required: false,
    default: null,
  },
  userEmail: {
    type: String,
    required: false,
    default: null,
  },
  type: {
    type: String,
    required: true,
  },
  acceptedAt: {
    type: String,
    required: false,
    default: null,
  },
  meta: Metadata,
};
