import { Bot } from '../services/BotService';

const fileIds = (bot: Bot): string[] => {
  return [bot?.imageFileId ?? ''];
};

type Brand = 'standalone' | 'portal' | 'allan';

const portalUrl = (baseUrl: string, botId: string, brand: Brand = 'standalone'): string => {
  return `${baseUrl}/${brand}/${botId}`;
};

const BotUtil = {
  fileIds,
  portalUrl,
};

export default BotUtil;
