import { Sync } from '../services/SyncService';

const isQueued = (sync?: Sync): boolean => {
  if (!sync) return false;
  if (sync.info.total === 0) return true;
  return false;
};

const isRunning = (sync?: Sync): boolean => {
  if (!sync) return false;
  if (sync.info.total === 0) return true;

  return sync.info.completed + sync.info.failed < sync.info.total;
};

const isSyncing = (sync?: Sync): boolean => {
  return isQueued(sync) || isRunning(sync);
};

const SyncUtil = {
  isQueued,
  isRunning,
  isSyncing,
};

export default SyncUtil;
